mapper.insert:
INSERT INTO USERQNA_INFO ( USERQNA_NUM, USER_NO, USERQNA_TITLE, USERQNA, CREATEDATE
, USERQNA_IMG
) VALUES ( USERQNA_NUM_SEQ.NEXTVAL, #{userNo}, #{userQnaTitle}, #{userQna}, SYSDATE
, #{userQnaImg}
)

mapper.update:
UPDATE USERQNA_INFO 
SET 
    USERQNA_TITLE = #{userQnaTitle},
    USERQNA = #{userQna},
    STATUS = #{status}
    
    , USERQNA_IMG = #{userQnaImg}
    
WHERE USERQNA_NUM = #{userQnaNum} 
  AND USER_NO = #{userNo}

mapper.select:
SELECT NVL(MAX(USERQNA_NUM), 0)
FROM USERQNA_INFO 
WHERE USER_NO = #{userNo}
